package com.assignmentsloopingstatements;

public class UsingFloat {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
      
		for(float f =0.5f;f<5F;f++) {
			System.out.println(f);
		}
		
		
		for(float f=0.23f;f<3.5f;f+=0.5f) {
			System.out.println(f);
		}
		
		
		for(float f=0f;f<1.0f;f+=0.1f) {
			System.out.println(f);
		}
		
		for(float f=0;f<5;f+=0.5) {
			System.out.println(f);
		}
		
		
	}

}
