package com.loopingstatements;

public class While {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int i = 0;
		while (i != 10) {
			System.out.print(i);
			i++;
		}
		
		System.out.println();
		
		
		int j=10;
		while(j!=0) {
			System.out.print(j);
			j--;
		}
		System.out.println();
		
	  int k =10;
	  do {
		  System.out.println(k--);
	  }
		while(k!=0);	
	

   }
}
